import { Component, OnInit } from '@angular/core';
import { AuthService } from '../_services/auth.service';
import { StorageService } from '../_services/storage.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit {
  form: any = {
    username: null,
    password: null
  };
  isLoggedIn = false;
  isLoginFailed = false;
  errorMessage = '';
  roles: string[] = [];

  constructor(private authService: AuthService, private storageService: StorageService) { }

  ngOnInit(): void {
    if (this.storageService.isLoggedIn()) {
      this.isLoggedIn = true;
      this.roles = this.storageService.getUser().roles;
    }
  }

  onSubmit(): void {
    const { username, password } = this.form;

    this.authService.login(username, password).subscribe({
      next: data => {
        this.storageService.saveUser(data);

        this.isLoginFailed = false;
        this.isLoggedIn = true;
        this.roles = this.storageService.getUser().roles;
        this.reloadPage();
      },
      error: err => {
        this.errorMessage = err.error.message;
        this.isLoginFailed = true;
      }
    });
  }

  reloadPage(): void {
    window.location.reload();
  }
}


<div class="fullcontent">
  <div class="headings">
      <h1>The Engineering Book Store</h1>
      <h2>Welcome to the engineering book store.Here you  can find  all the books related to engineering.</h2>
  </div>
  <div class="formbox">
      <h3>Log In</h3>
      <form id="form" action="" method="post">
          <label for="">Username</label>
          <input type="text" name="username" class="asd"/>
          <label for="">Password</label>
          <input type="password" id="" name="password" class="asd"/>
          <input id="btn" type="submit" name="submit" value="Log In" class="mainbox">
          <label for="">New customer?</label>
          <a href="register.html" class="mainbox">Register Now</a>
      </form>
  </div>
</div>








